const axios = require('axios');

const RedisHandlerURL = 'http://localhost:5002';

const FetchRequests = async (channelId, address, request_type) => {
    try {
        var bodyFormData = new FormData();
        bodyFormData.set('channel_id', channelId);
        bodyFormData.set('address', address);
        bodyFormData.set('request_type', request_type);
        const resp = await axios({
            method: 'post',
            url: RedisHandlerURL + '/fetch_request',
            data: bodyFormData
        })
        if (resp.status === 200) {
            return resp.data;
        }
        return null;
    } catch (error) {
        console.log("Redis connectivity issue")
        console.log(error)
    }
}

const SetRequest = async (channelId, address, request_type, request_data) => {
    try {
        var bodyFormData = new FormData();
        bodyFormData.set('channel_id', channelId);
        bodyFormData.set('address', address);
        bodyFormData.set('request_type', request_type);
        bodyFormData.set('request_data', request_data);
        const resp = await axios({
            method: 'post',
            url: RedisHandlerURL + '/set_request',
            data: bodyFormData
        })
        if (resp.status === 200) {
            return resp.data;
        }
        return null;
    } catch (error) {
        console.log("Redis connectivity issue")
        console.log(error)
    }
}

const UpdateRequest = async (channelId, theOtherParty) => {
    const sender = await FetchAccount()
    console.log(sender, web3.utils.checkAddressChecksum(sender))
    try {
        var txHash = await sideChainContract.methods.openChannel(channelId, web3.utils.toChecksumAddress(theOtherParty))
            .send({ from: web3.utils.toChecksumAddress(sender), gas: 200000 })
        console.log('new channel created, txHash->', txHash.transactionHash);
    } catch (error) {
        console.log("Open Channel request reverted")
        console.log(error)
    }
}

const DeleteRequest = async (channelId, cash) => {
    try {
        const sender = await FetchAccount()
        let flag = await ChannelExists(channelId)
        if (flag) {
            toast.error(`Channel with ${channelId} doesn't exists`)
        } else {
            var txHash = await sideChainContract.methods.confirm(channelId)
                .send({ from: web3.utils.toChecksumAddress(sender), value: web3.utils.toHex(web3.utils.toWei(cash, 'wei')), gas: 200000 })
            console.log('Channel Deposit Confirmed. txHash-> ', txHash.transactionHash);
            toast.success("Deposit confirmed")
        }
    } catch (error) {
        console.log("Channel Deposit request reverted")
        console.log(error)
        toast.error(`Deposit Declined for Channel Id ${channelId}`)
    }
}

export { FetchRequests, SetRequest, UpdateRequest, DeleteRequest };
