import React from 'react';
import { GetChannelDetails, ChannelConfirm } from '../contract/contract_transaction';
// import { toast } from 'react-toastify'

class ConfirmDeposit extends React.Component {
    state = {
        channelId: "",
        error: "",
        amount: '',
    };
    handleChannelDetails = async (event) => {
        event.preventDefault();
        GetChannelDetails(this.state.channelId)
        this.setState({ 'channelId': "" })


    }

    handleConfirmChannel = async (event) => {
        event.preventDefault();
        console.log(this.state.channelId)
        if (Number.isNaN(this.state.channelId)) {
            this.setState({ 'error': "Enter Interger channel Id" })
        }
        else {
            ChannelConfirm(this.state.channelId, this.state.amount)
        }
        this.setState({ 'channelId': "" })
        this.setState({ 'amount': "" })


    }
    render() {
        return (
            <div>
                <form onSubmit={this.handleConfirmChannel}>
                    <div className="form-group">
                        <label htmlFor="channelId">Channel Id</label>
                        <div className="field">
                            <input
                                id="channelId"
                                type="text"
                                onChange={event => this.setState({ channelId: event.target.value })}
                                name="channelId"
                                className="form-control"
                                value={this.state.channelId}
                                required
                            />
                        </div>
                        {this.state.error && <div className="alert alert-danger">{this.state.error}</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Channel Id</label>
                        <div className="field">
                            <input
                                id="amount"
                                type="text"
                                onChange={event => this.setState({ amount: event.target.value })}
                                name="amount"
                                className="form-control"
                                value={this.state.amount}
                                required
                            />
                        </div>
                        {this.state.error && <div className="alert alert-danger">{this.state.error}</div>}
                    </div>
                    <input type="submit" value="Submit" className="btn btn-primary" />
                </form>


                {/* <form onSubmit={this.handleChannelDetails}>
                    <label htmlFor="count">Channel Id</label>
                    {"   "}
                    <input type="text" placeholder="channel Id" value={this.state.channelId}
                        onChange={event => this.setState({ channelId: event.target.value })} required />
                    {"   "}
                    <button>GetChannelDetails</button>
                </form>

                <form onSubmit={this.handleConfirmChannel}>
                    <label htmlFor="count">Channel Id</label>
                    {"   "}
                    <input type="text" placeholder="channel Id" value={this.state.channelId2}
                        onChange={event => this.setState({ channelId2: event.target.value })} required />
                    {"   "}
                    <button>ChannelConfirm</button>
                </form> */}
            </div>
        )
    }
}


export default ConfirmDeposit;
