import React, { useState } from "react";
// import courseStore from "../stores/courseStore";
// import { toast } from "react-toastify";
// import * as courseActions from "../actions/courseActions";
import { GetChannelDetails } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";

const ChannelDetailsPage = props => {
    const [errors, setErrors] = useState(false);
    const [channelId, setChannelId] = useState("");
    const [details, setDetails] = useState("");
    const [value, setValue] = React.useState('');
    // const [course, setCourse] = useState({
    //     id: null,
    //     slug: "",
    //     title: "",
    //     authorId: null,
    //     category: ""
    // });
    React.useEffect(() => {
        localStorage.setItem('myValueInLocalStorage', value);
    }, [value]);
    const onChange = event => setValue(event.target.value);

    function formIsValid() {
        let _errors = '';

        if (channelId < 0) _errors = "Provide Positive Interger Value";
        if (channelId === '') _errors = "Provide Positive Interger Value";

        setErrors(_errors);
        // Form is valid if the errors object has no properties
        return _errors === '';
    }

    async function handleSubmit(event) {
        event.preventDefault();
        if (!formIsValid()) return;
        let value = await GetChannelDetails(channelId)
        setDetails(value);
        console.log("after click ", value)
    }

    return (
        <>
            <h2>Channel Details</h2>
            <div>
                <div>
                    <h1>Hello React with Local Storage!</h1>
                    <input value={value} type="text" onChange={onChange} />
                    <p>{value}</p>
                </div>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="channelId">Channel Id</label>
                    <input id="channelId"
                        className="form-control"
                        type="text"
                        placeholder="channel id(integer value)" value={channelId}
                        onChange={event => setChannelId(event.target.value)} required />
                    <button>Get Channel Details</button>
                </form>
                {errors && (
                    <div className="alert alert-danger">{errors}</div>
                )}
                {(errors === '') && (
                    <div className="alert alert-danger">
                        <h2>Channel Details of channel id -> {channelId}</h2>
                        <ManageDetailsView details={details} />
                    </div>
                )}
            </div>
        </>
    );
};

export default ChannelDetailsPage;
