}

class PendingState extends React.Component {
    state = {
        channelId: "",
        channelIdError: "",
        amount: '',
        amountError: "",
        pendingStates: JSON.parse(localStorage.getItem("pendingState") || "[]"),
    };
    handleConfirmChannel = async (event) => {
        event.preventDefault();
        // this.setState({ 'channelIdError': "" })
        // this.setState({ 'amountError': "" })
        // if (Number.isNaN(parseInt(this.state.channelId, 10))) {
        //     this.setState({ 'channelIdError': "Enter Interger channel Id" })
        // }
        // else if (Number.isNaN(parseInt(this.state.amount, 10))) {
        //     this.setState({ 'amountError': "Enter Integer amount(in wei unit)" })
        // }
        // else {
        let response = await FetchRequests(1, "0x19DbcfB1E970EB0ac300936824de350EE248e803", 1)
        console.log(response)
        localStorage.setItem('pendingState', JSON.stringify(response));
        // }
        // this.setState({ 'channelId': "" })
        // this.setState({ 'amount': "" })
    }
    render() {
        return (
            <div>
                <h2>Pending States</h2>
                <div>
                    <h1>Hello React with Local Storage!</h1>
                    <form id="form" onSubmit={this.handleConfirmChannel}>
                        <input type="submit" value="Refresh Pending States" className="btn btn-primary" />
                    </form>
                    {console.log(this.state.pendingStates)}
                    <FetchPendingState states={this.state.pendingStates} />
                </div>
            </div>
        )
    }
};
export default PendingState;
