import React from 'react';
import { GetChannelDetails, ChannelConfirm } from '../contract/contract_transaction';
// import { toast } from 'react-toastify'

class ConfirmDeposit extends React.Component {
    state = {
        channelId: "",
        error: "",
        amount: '',
    };
    handleChannelDetails = async (event) => {
        event.preventDefault();
        GetChannelDetails(this.state.channelId)
        this.setState({ 'channelId': "" })


    }

    handleConfirmChannel = async (event) => {
        event.preventDefault();
        console.log(this.state.channelId2)
        ChannelConfirm(this.state.channelId2)
        this.setState({ 'channelId2': "" })


    }
    render() {
        return (
            <div>
                <form>
                    <div className="form-group">
                        <label htmlFor="channelId">Channel Id</label>
                        <div className="field">
                            <input
                                id="channelId"
                                type="text"
                                onChange={event => this.setState({ channelId: event.target.value })}
                                name="channelId"
                                className="form-control"
                                value={this.state.channelId}
                            />
                        </div>
                        {this.state.error && <div className="alert alert-danger">{this.state.error}</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Channel Id</label>
                        <div className="field">
                            <input
                                id="amount"
                                type="text"
                                onChange={event => this.setState({ amount: event.target.value })}
                                name="amount"
                                className="form-control"
                                value={this.state.amount}
                            />
                        </div>
                        {this.state.erro && <div className="alert alert-danger">{this.state.erro}</div>}
                    </div>
                    <input type="submit" value="Save" className="btn btn-primary" />
                </form>


