                </form>

                <form onSubmit={this.handleConfirmChannel}>
                    <label htmlFor="count">Channel Id</label>
                    {"   "}
                    <input type="text" placeholder="channel Id" value={this.state.channelId2}
                        onChange={event => this.setState({ channelId2: event.target.value })} required />
                    {"   "}
                    <button>ChannelConfirm</button>
                </form> */}
            </div>
        )
    }
}


export default ConfirmDeposit;
