import React from 'react';
import { GetChannelDetails, CreateNewChannel, ChannelConfirm } from '../contract/contract_transaction';
// import { toast } from 'react-toastify'

class ConfirmDeposit extends React.Component {
    state = {
        channelId: "",
        channelId1: "",
        channelId2: "",
    };
    handleChannelDetails = async (event) => {
        event.preventDefault();
        console.log(this.state.channelId)
        GetChannelDetails(this.state.channelId)
        this.setState({ 'channelId': "" })


    }

    handleConfirmChannel = async (event) => {
        event.preventDefault();
        console.log(this.state.channelId2)
        ChannelConfirm(this.state.channelId2)
        this.setState({ 'channelId2': "" })


    }
    render() {
        return (
            <div>
                <form onSubmit={this.handleChannelDetails}>
                    <label htmlFor="count">Channel Id</label>
                    {"   "}
                    <input type="text" placeholder="channel Id" value={this.state.channelId}
                        onChange={event => this.setState({ channelId: event.target.value })} required />
                    {"   "}
                    <button>GetChannelDetails</button>
                </form>

                <form onSubmit={this.handleConfirmChannel}>
                    <label htmlFor="count">Channel Id</label>
                    {"   "}
                    <input type="text" placeholder="channel Id" value={this.state.channelId2}
                        onChange={event => this.setState({ channelId2: event.target.value })} required />
                    {"   "}
                    <button>ChannelConfirm</button>
                </form>
            </div>
        )
    }
}


export default ConfirmDeposit;
