import React from "react";
// import { toast } from "react-toastify";
import { GetChannelDetails, IsMyChannel } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
import ChannelList from "./ChannelList";
import { FetchAccount } from "./MetaMaskFetch";
import ActionList from "./ActionList";
import OnChainStateView from "./OnChainStateView";
import OffChainStateView from "./OffChainState";
import { FetchOffChainDetails } from "../LocalStorage/local_storage_api";

class ActionConfirmationPage extends React.Component {
    state = {
        errors: false,
        channelId: "",
        onChainState: false,
        offChainState: false,
        formFlag: false,
        sender: "",
        action: '',
    }

    componentDidMount() {
        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
                console.log(this.state.sender)
                // let key = this.state.sender + 'active_channel_ids'
                this.setState({ 'channelId': this.props.location.state.channelId })
                this.setState({ 'action': this.props.location.state.action })
                console.log(this.state.action, this.state.channelId)
                if (this.state.action === 'New Transaction') {
                    let offChainState = FetchOffChainDetails(this.state.sender, this.state.channelId)
                    this.setState({ 'offChainState': offChainState })
                    this.setState({ 'formFlag': true })
                }
                else {

                }
            }
        }).catch(err => {
            alert("check metamask connectivity")
            console.log(err)
        })
    }

    onActionChange = async (event) => this.setState({ 'action': event.target.value });

    actionHandler = async (event) => {
        event.preventDefault();
        if (this.state.sender === undefined || this.state.sender === null) {
            alert("Check MetaMask Connectivity")
        }
        this.setState({ 'errors': false });
        console.log("this.state.action", this.state.action)
        if (this.state.action === 'select action' || this.state.action === '') {
            this.setState({ 'errors': "Please select action from drop down" })
        }
        else {
            let url = "/waitingChannel/" + this.state.waitingChannelId
            console.log(url)
            // console.log(this.props);
            this.props.history.push(url);
            this.props.history.push({
                pathname: '/actionconfirmation',
                search: '?query=' + this.state.channelId,
                state: {
                    channelId: this.state.channelId,
                    action: this.state.action
                }
            })
        }
    }



    render() {
        return (
            <>
                <h2>Channel Id: {this.state.channelId}</h2>
                <div>
                    <div>
                        {this.state.onChainState && <OnChainStateView details={this.state.onChainState} />}
                    </div>
                    <div>
                        {this.state.offChainState && <OffChainStateView details={this.state.offChainState} />}
                    </div>
                    <div>
                        {this.state.formFlag && <table id="table" className="table">
                            <thead>
                                <tr>
                                    <th>Type</th>
                                    <th>value</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr key="Channel Id">
                                    <td><span>Channel Id</span></td>
                                    <td><span>{}</span></td>
                                </tr>
                                <tr key="Transaction Count">
                                    <td ><span>Transaction Count</span></td>
                                    <td><span>{}</span></td>
                                </tr>
                                <tr key="Alice Cash">
                                    <td ><span>Alice Cash</span></td>
                                    <td><span>{}</span></td>
                                </tr>
                                <tr key="Bob Cash">
                                    <td ><span>Bob Cash</span></td>
                                    <td><span>{}</span></td>
                                </tr>
                            </tbody>
                        </table>}
                    </div>
                    <form id="form" onSubmit={this.actionHandler}>
                        <ActionList onChange={this.onActionChange} />
                        {this.state.errors && <div className="alert alert-danger">{this.state.errors}</div>}
                        <button id="button">check Active Channel Status</button>
                    </form>
                </div>

            </>
        );
    }
};

export default ActionConfirmationPage;
