import React from "react";
// import { toast } from "react-toastify";
import { GetChannelDetails } from "../contract/contract_transaction";
import { FetchAccount } from "./MetaMaskFetch";
import OnChainStateView from "./OnChainStateView";

class RefundChannel extends React.Component {
    state = {
        errors: false,
        channelId: "",
        onChainState: false,
        formFlag: false,
        sender: "",
        action: '',
        aliceCash: '',
        bobCash: '',
    }

    componentDidMount() {
        FetchAccount().then(sender => {
            if (sender) {
                console.log(this.props.location.state.action, this.props.location.state.channelId)
                if (this.props.location.state.action === 'Refund') {
                    GetChannelDetails(this.props.location.state.channelId).then(onChainState => {
                        this.setState({
                            'sender': sender,
                            'channelId': this.props.location.state.channelId,
                            'action': this.props.location.state.action,
                            'onChainState': onChainState,
                            'formFlag': true,
                        })
                    }).catch(err => {
                        alert("on chain state issue")
                        console.log(err)
                    })
                }
                else {
                    alert("go back ")
                }
            }
        }).catch(err => {
            alert("check metamask connectivity")
            console.log(err)
        })
    }

    handleRefund = async (event) => {
        event.preventDefault();
        this.setState({ 'errors': false })
        if (Number.isNaN(parseInt(this.state.aliceCash, 10))) {
            this.setState({ 'errors': "Enter Integer amount(in wei unit)" })
        }
        else if (Number.isNaN(parseInt(this.state.bobCash, 10))) {
            this.setState({ 'errors': "Enter Integer amount(in wei unit)" })
        }
        else {

        }
        this.setState({ 'aliceCash': "" })
        this.setState({ 'bobCash': "" })


    }

    render() {
        return (
            <>
                <h2>Channel Id: {this.state.channelId}</h2>
                <div>
                    <div>
                        {this.state.onChainState && <OnChainStateView details={this.state.onChainState} />}
                    </div>

                    <div>
                        {this.state.formFlag &&
                            <form id="form" className="mt-2" onSubmit={this.handleRefund}>
                                <input type="submit" value="Refund Channel" className="btn btn-primary" />
                            </form>}
                    </div>
                </div>

            </>
        );
    }
};

export default RefundChannel;
