import React from "react";
// import PropTypes from "prop-types";
// import { Link } from "react-router-dom";
// import { toast } from "react-toastify";
import './UserList.css'
import { FetchRequests, DeleteRequest, SetRequest } from "../redisApi/GetSetData";
import { FetchAccount } from "./MetaMaskFetch";
import { FetchOtherParty, IsValidSignature } from "../contract/contract_transaction";
import STATE_TYPE from "../RequestType";
import GenerateSignatures from "../contract/CreateSignature";


async function onDeleteClick(index) {
    const sender = await FetchAccount()
    let response = await DeleteRequest(sender, STATE_TYPE.Request, index)
    console.log(response)
}

async function onShowLastState(sender, request) {
    let channel_id = request.split('_')[0]
    let key = sender + channel_id + "_last_signed_state"
    alert(localStorage.getItem(key) || "No Signed State Defined")
}

async function onAcceptClick(request, index) {
    let splitstate = request.split("_");
    let state_obj = {
        'Channel Id': splitstate[0],
        'count': splitstate[1],
        'Alice Cash': splitstate[2],
        'Bob Cash': splitstate[3],
        'sig': splitstate[4],
    }
    let state = JSON.parse(request)
    let channel_id = Number.parseInt(state[0])
    let count = Number.parseInt(state[1])
    let Alice_Cash = Number.parseInt(state[2])
    let Bob_Cash = Number.parseInt(state[3])
    let signature = state[4]

    console.log(channel_id, count, Alice_Cash, Bob_Cash, signature)

    const sender = await FetchAccount()
    const TheOtherParty = await FetchOtherParty(channel_id, sender)
    let checkSig = await IsValidSignature(TheOtherParty, channel_id, count, Alice_Cash, Bob_Cash, signature)

    if (checkSig === true) {
        signature = await GenerateSignatures(channel_id, count, Alice_Cash, Bob_Cash)
        let key = sender + channel_id + "laststate"
        localStorage.setItem(key, JSON.stringify(request));
        let lastState = [
            channel_id, count, Alice_Cash, Bob_Cash, signature
        ]
        let response = await SetRequest(TheOtherParty, STATE_TYPE.Response, JSON.stringify(lastState))
        console.log("hello ", response)
        alert("valid signature")
    }
    else {
        alert("invalid signature")
    }
}

class PendingRequests extends React.Component {
    state = {
        pendingStateRequests: null,
        sender: null,
        // pendingStates: JSON.parse(localStorage.getItem("pendingState") || "[]"),
    };

    componentDidMount() {
        FetchAccount()
            .then(sender => {
                if (sender) {
                    this.setState({ 'sender': sender })
                    FetchRequests(this.state.sender, STATE_TYPE.Request)
                        .then(response => {
                            console.log(response)
                            let key = this.state.sender + 'pendingRequests'
                            localStorage.setItem(key, JSON.stringify(response));
                            this.setState({ pendingStateRequests: response })
                        })
                        .catch(err => {
                            alert("error occured while fetching pending requests")
                            console.log(err)
                        })

                }

            })
            .catch(err => {
                alert("check metamask connectivity")
                console.log(err)
            })
    }



    render() {
        return (
            <div>
                <h2>New State Requests</h2>
                <div>
                    <table id="table" className="table">
                        <thead>
                            <tr>
                                <th>Decline State</th>
                                <th>Accept State</th>
                                <th>Channel Id</th>
                                <th>Count</th>
                                <th>Alice Cash</th>
                                <th>Bob Cash</th>
                                <th>Last Signed State</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.pendingStateRequests && Object.keys(this.state.pendingStateRequests).map(request => {
                                    return (
                                        <tr key={request}>
                                            <td>
                                                <button className="btn btn-outline-danger" onClick={() => onDeleteClick(request)} >
                                                    Delete Request
                                                </button>
                                            </td>
                                            <td>
                                                <button className="btn btn-outline-danger" onClick={() => onAcceptClick(this.state.pendingStateRequests[request], request)} >
                                                    Accept Request
                                                </button>
                                            </td>
                                            <td><span>{this.state.pendingStateRequests[request].split('_')[0]}</span></td>
                                            <td><span>{this.state.pendingStateRequests[request].split('_')[1]}</span></td>
                                            <td><span>{this.state.pendingStateRequests[request].split('_')[2]}</span></td>
                                            <td><span>{this.state.pendingStateRequests[request].split('_')[3]}</span></td>
                                            <td>
                                                <button className="btn btn-outline-danger" onClick={() => onShowLastState(this.state.sender, this.state.pendingStateRequests[request])} >
                                                    Show Last Signed State
                                                </button>
                                            </td>
                                        </tr>
                                    )
                                }
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
};
export default PendingRequests;
