import Web3 from 'web3'

const checkMetaMask = async () => {
    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545")
    return web3
}

function getAccount() {
    let web3Instance = checkMetaMask()
    console.log("default Account ", web3Instance.eth.accounts[0])
    return web3Instance.eth.accounts[0];
}
export {
    checkMetaMask,
    getAccount
};
