import React, { useState } from "react";
// import courseStore from "../stores/courseStore";
// import { toast } from "react-toastify";
// import * as courseActions from "../actions/courseActions";
import { GetChannelDetails, GetReceipt } from "../contract/contract_transaction";
// import ManageDetailsView from "./ManageDetailsView";
// import { fetchLastState } from "../Api/FetchBrowserData";
// import ChannelList from "./ChannelList";
import FetchListeningAddr from "../Api/FetchListeningAddr";
// import EditFiles from "../redisApi/PubSub";
// import testJSON from "../redisApi/nodecheck";
// import RedisConnection from "../redisApi/nodecheck";

const RedisFunctionalTesting = props => {
    const [errors, setErrors] = useState(false);
    const [channelId, setChannelId] = useState("");
    const [details, setDetails] = useState("");
    const [value, setValue] = useState("");
    const [channels, setChannels] = React.useState(JSON.parse(localStorage.getItem("PayMentChannels") || "[]"));
    // const [course, setCourse] = useState({
    //     id: null,
    //     slug: "",
    //     title: "",
    //     authorId: null,
    //     category: ""
    // });

    async function onChange(event) {
        event.preventDefault();
        // channels.push(value)
        // alert(channels)
        // localStorage.setItem('PayMentChannels', JSON.stringify(channels));
        await GetReceipt()
        await FetchListeningAddr(value)
        // localStorage.removeItem('Tempchannels')
    }
    const onValueChange = event => setValue(event.target.value);

    async function handleSubmit(event) {
        event.preventDefault();
        setErrors("");
        if (Number.isNaN(parseInt(channelId, 10))) {
            setErrors("Enter Interger channel Id")
        }
        else {
            let value = await GetChannelDetails(channelId)
            setDetails(value);
        }
    }



    const onOptionChange = event => setChannelId(event.target.value)
    return (
        <>
            <h2>Channel Details</h2>
            <div>
                <div>
                    <h1>Redis functional Testing</h1>
                </div>
                <form id="form" className="mt-2" onSubmit={onOptionChange}>
                    <div className="form-group">
                        <label htmlFor="Count">Count</label>
                        <div className="field">
                            <input
                                id="Count"
                                readOnly
                                type="text"
                                name="Count"
                                className="form-control"
                                value={this.state.lastState[1]}
                            />
                        </div>
                    </div>
                    <div className="form-group">
                        <label htmlFor="aliceCash">Alice Cash</label>
                        <div className="field">
                            <input
                                id="aliceCash"
                                type="text"
                                onChange={event => this.setState({ aliceCash: event.target.value })}
                                name="aliceCash"
                                className="form-control"
                                value={this.state.aliceCash}
                            />
                        </div>
                        {/* {this.state.amountError && <div className="alert alert-danger">{this.state.amountError}</div>} */}
                    </div>
                    <div className="form-group">
                        <label htmlFor="bobCash">Bob Cash</label>
                        <div className="field">
                            <input
                                id="bobCash"
                                type="text"
                                onChange={event => this.setState({ bobCash: event.target.value })}
                                name="bobCash"
                                className="form-control"
                                value={this.state.bobCash}
                            />
                        </div>
                        {this.state.errors && <div className="alert alert-danger">{this.state.errors}</div>}
                    </div>
                    <input type="submit" value="Submit Transaction" className="btn btn-primary" />
                </form>
                {(errors === '') && (
                    <div >
                        <h2>Channel Details of channel id -> {channelId}</h2>
                        {/* <ManageDetailsView details={details} /> */}
                    </div>
                )}
            </div>
        </>
    );
};

export default RedisFunctionalTesting;
