import React, { useEffect, useState } from 'react';
import bg from './BG.jpeg';
import './App.css';
import { CreateNewChannel } from './contract/contract_transaction';
import ConfirmDeposit from './components/ConfirmDeposit';
import UserList from './components/AddUser';
import { Users } from "./mockData/mockUsers";
import ChannelDetailsPage from './components/ChannelDetailsPage';
import { ToastContainer } from 'react-toastify';
import Header from './Header';

const App = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const imageUrl = windowWidth >= 650 ? bg : bg;

  const handleWindowResize = () => {
    setWindowWidth(window.innerWidth);
  };

  useEffect(() => {
    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
    }
  });

  return (
    <div className="App" style={{ height: window.innerWidth, backgroundImage: `url(${imageUrl})` }}>
      <div className="container-fluid">
        <ToastContainer autoClose={3000} hideProgressBar />
        <Header />
        <Switch>
          <Route path="/" exact component={HomePage} />
          <Route path="/courses" component={CoursesPage} />
          <Route path="/about" component={AboutPage} />
          <Route path="/course/:slug" component={ManageCoursePage} />
          <Route path="/course" component={ManageCoursePage} />
          <Route path="/setidentity" component={ManageIdentityPage} />
          <Redirect from="/about-page" to="about" />
          <Route path="/plasmacash" component={PlasmaCash} />
          <Route path="/multichannel" component={ChannelApp} />
          <Route path="/transaction" component={ConfirmDeposit} />
          <Route path="/pending_request" component={PendingState} />

          <Route component={NotFoundPage} />
        </Switch>
        <h1>Shipment Application</h1>
        <ChannelDetailsPage />
        <p>Hakuna Matata</p>
        <ConfirmDeposit />
        <UserList users={Users} createNewChannel={CreateNewChannel} />
      </div>
    </div>
  );

};

export default App;
