import React from 'react';
import { contract_address, contract_abi } from "./contract_config";
// import { Web3 } from "web3";


// const Tx = require('ethereumjs-tx')
const Web3 = require('web3')
const web3 = new Web3(new Web3(new Web3.providers.HttpProvider("http://localhost:8545")))

const sideChainContract = new web3.eth.Contract(contract_abi, contract_address)

const FilterEvents = async (props) => {

    if (props.sender === undefined || props.sender === null) {
        alert("Check MetaMask Connectivity")
    }
    else {
        let key = props.sender + "last_fetch_block"
        let START_BLOCK = localStorage.getItem(key)
        START_BLOCK = parseInt(START_BLOCK, 10)

        if (Number.isNaN(START_BLOCK)) {
            START_BLOCK = 0
        }
        const END_BLOCK = await web3.eth.getBlockNumber()
        localStorage.setItem(key, END_BLOCK)
        console.log(START_BLOCK, END_BLOCK)
        if (START_BLOCK < END_BLOCK) {
            sideChainContract.getPastEvents("allEvents",
                {
                    fromBlock: START_BLOCK,
                    toBlock: END_BLOCK // You can also specify 'latest'
                })
                .then(events => console.log(events))
                .catch((err) => console.error(err));
        }
        else {
            return null
        }
    }

}

export default FilterEvents;
