import React from "react";
// import { toast } from "react-toastify";
import { GetChannelDetails, IsMyChannel } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
import ChannelList from "./ChannelList";
import { FetchAccount } from "./MetaMaskFetch";
import ActionList from "./ActionList";
import OnChainStateView from "./OnChainStateView";

class ActiveChannelPage extends React.Component {
    state = {
        errors: false,
        channelId: "",
        onChainState: "",
        offChainState: "",
        value: "",
        operation: "",
        sender: "",
        channels: [],
        action: '',
    }

    componentDidMount() {
        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
                console.log(this.state.sender)
                let key = this.state.sender + 'active_channel_ids'
                this.setState({ 'channels': JSON.parse(localStorage.getItem(key) || "[]") })
                this.setState({ 'channelId': this.props.match.params.channel_id })
                console.log("channel Id", this.state.channelId);
                this.fetchOnChainDetails().then(onChainState => {
                    this.setState({ 'onChainState': onChainState })
                }).catch(err => {
                    alert("on chain state issue")
                    console.log(err)
                })
                this.fetchOffChainDetails().then(offChainState => {
                    this.setState({ 'offChainState': offChainState })
                }).catch(err => {
                    alert("off chain state issue")
                    console.log(err)
                })
            }

        }).catch(err => {
            alert("check metamask connectivity")
            console.log(err)
        })
    }

    fetchOnChainDetails = async (event) => {
        let value = await GetChannelDetails(this.state.channelId)
        return value;
    }

    fetchOffChainDetails = async (event) => {
        let key = this.state.sender + this.state.channelId + '_last_signed_state'
        let value = localStorage.getItem(key)
        if (value !== null && value !== undefined) {
            let splitstate = value.split("_");
            let state_obj = {
                'Channel Id': splitstate[0],
                'count': splitstate[1],
                'Alice Cash': splitstate[2],
                'Bob Cash': splitstate[3],
            }
            console.log(state_obj)
            return state_obj
        }
        return "No off chain state Found"
    }

    onActionChange = async (event) => this.setState({ 'action': event.target.value });

    actionHandler = async (event) => {
        event.preventDefault();
        if (this.state.sender === undefined || this.state.sender === null) {
            alert("Check MetaMask Connectivity")
        }
        this.setState({ 'errors': false });
        console.log("this.state.action", this.state.action)
        if (this.state.action === 'select action' || this.state.action === '') {
            this.setState({ 'errors': "Please select action from drop down" })
        }
        else {
            let url = "/waitingChannel/" + this.state.waitingChannelId
            console.log(url)
            // console.log(this.props);
            // this.props.history.push(url);
        }
    }


    render() {
        return (
            <>
                <h2>Channel Id: {this.state.channelId}</h2>
                <div>
                    <div>
                        <p>On Chain State</p>
                        <ManageDetailsView details={this.state.onChainState} />
                    </div>
                    <div>
                        <p>Off Chain State</p>
                        {(typeof this.state.offChainState === 'string') && <p>{this.state.offChainState}</p>}
                        {(typeof this.state.offChainState !== 'string') && <OnChainStateView details={this.state.offChainState} />}
                    </div>
                    <form id="form" onSubmit={this.actionHandler}>
                        <ActionList onChange={this.onActionChange} />
                        {this.state.errors && <div className="alert alert-danger">{this.state.errors}</div>}
                        <button id="button">check Active Channel Status</button>
                    </form>
                </div>

            </>
        );
    }
};

export default ActiveChannelPage;
