import React, { useEffect, useState } from 'react';
import bg from './BG.jpeg';
import './App.css';
import { CreateNewChannel, Channel } from './contract/contract_transaction';
import ConfirmDeposit from './components/ConfirmDeposit';
import AddUser from './components/AddUser';


const App = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const imageUrl = windowWidth >= 650 ? bg : bg;

  const handleWindowResize = () => {
    setWindowWidth(window.innerWidth);
  };

  const HandleChannelCreation = (props) => {
    CreateNewChannel(props.ChannelId)
  }

  useEffect(() => {
    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
    }
  });

  return (
    <div className="App" style={{ backgroundImage: `url(${imageUrl})` }}>
      <div className="App-content">
        <h1>Shipment Application</h1>
        <p>Hakuna Matata</p>
        <Channel />
        <ConfirmDeposit />
        <AddUser />
      </div>
    </div>
  );

};

export default App;
