import React from "react";
// import { toast } from "react-toastify";
import { IsValidSignature, FetchOtherParty, GetChannelDetails, ChannelConfirm } from "../contract/contract_transaction";
import { FetchAccount } from "./MetaMaskFetch";
import OnChainStateView from "./OnChainStateView";
import OffChainStateView from "./OffChainState";
import { FetchOffChainDetails, MakeStringState } from "../LocalStorage/local_storage_api";
import GenerateSignatures from "../contract/CreateSignature";
import { SetRequest } from "../redisApi/GetSetData";
import STATE_TYPE from "../RequestType";

class ConfirmChannel extends React.Component {
    state = {
        errors: false,
        channelId: "",
        onChainState: false,
        offChainState: false,
        formFlag: false,
        sender: "",
        action: '',
        cash: '',
        bobCash: '',
    }

    componentDidMount() {
        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
                console.log(this.state.sender)
                // let key = this.state.sender + 'active_channel_ids'
                this.setState({ 'channelId': this.props.location.state.channelId })
                this.setState({ 'action': this.props.location.state.action })
                console.log(this.state.action, this.state.channelId)
                if (this.state.action === 'Confirm') {
                    GetChannelDetails(this.state.channelId).then(onChainState => {
                        this.setState({ 'onChainState': onChainState })
                        this.setState({ 'formFlag': true })
                    }).catch(err => {
                        alert("on chain state issue")
                        console.log(err)
                    })
                }
                else {
                    alert("go back ")
                }
            }
        }).catch(err => {
            alert("check metamask connectivity")
            console.log(err)
        })
    }

    onActionChange = async (event) => this.setState({ 'action': event.target.value });

    actionHandler = async (event) => {
        event.preventDefault();
        if (this.state.sender === undefined || this.state.sender === null) {
            alert("Check MetaMask Connectivity")
        }
        this.setState({ 'errors': false });
        console.log("this.state.action", this.state.action)
        if (this.state.action === 'select action' || this.state.action === '') {
            this.setState({ 'errors': "Please select action from drop down" })
        }
        else {
            let url = "/waitingChannel/" + this.state.waitingChannelId
            console.log(url)
            // console.log(this.props);
            this.props.history.push(url);
            this.props.history.push({
                pathname: '/actionconfirmation',
                search: '?query=' + this.state.channelId,
                state: {
                    channelId: this.state.channelId,
                    action: this.state.action
                }
            })
        }
    }

    handleConfirmChannel = async (event) => {
        event.preventDefault();
        this.setState({ 'errors': false })
        if (Number.isNaN(parseInt(this.state.cash, 10))) {
            this.setState({ 'errors': "Enter Integer amount(in wei unit)" })
        }
        else {
            ChannelConfirm(this.state.channelId, parseInt(this.state.cash))
        }
        this.setState({ 'cash': "" })
        this.setState({ 'bobCash': "" })
    }

    render() {
        return (
            <>
                <h2>Channel Id: {this.state.channelId}</h2>
                <div>
                    <div>
                        {this.state.onChainState && <OnChainStateView details={this.state.onChainState} />}
                    </div>
                    <div>
                        {this.state.formFlag &&
                            <form id="form" className="mt-2" onSubmit={this.handleConfirmChannel}>
                                <div className="form-group">
                                    <label htmlFor="cash">Input Cash</label>
                                    <div className="field">
                                        <input
                                            id="cash"
                                            type="text"
                                            onChange={event => this.setState({ cash: event.target.value })}
                                            name="cash"
                                            className="form-control"
                                            value={this.state.cash}
                                        />
                                    </div>
                                    {this.state.errors && <div className="alert alert-danger">{this.state.errors}</div>}
                                </div>
                                <input type="submit" value="Confirm Channel" className="btn btn-primary" />
                            </form>}
                    </div>
                </div>

            </>
        );
    }
};

export default ConfirmChannel;
