import React, { useState } from "react";
// import courseStore from "../stores/courseStore";
// import { toast } from "react-toastify";
// import * as courseActions from "../actions/courseActions";
import { GetChannelDetails } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
// import { fetchLastState } from "../Api/FetchBrowserData";
import ChannelList from "./ChannelList";

class ChannelDetailsPage extends React.Component {
    state = {
        errors: false,
        channelId: "",
        details: "",
        value: "",
        sender: "",
        channels: React.useState(JSON.parse(localStorage.getItem("PayMentChannels") || "[]")),

    }
    // const [course, setCourse] = useState({
    //     id: null,
    //     slug: "",
    //     title: "",
    //     authorId: null,
    //     category: ""
    // });

    onAddChange = async (event) => {
        event.preventDefault();
        var index = this.state.channels.indexOf(this.state.value);
        if (index < 0) {
            this.state.channels.push(this.state.value)
            localStorage.setItem('PayMentChannels', JSON.stringify(this.state.channels));
        }
        console.log("onAddChange Called ", this.state.value)
    }

    onDeleteChange = async (event) => {
        event.preventDefault();
        var index = this.state.channels.indexOf(this.state.value);
        if (index > -1) {
            this.state.channels.splice(index, 1);
        }
        // channels.push(value)
        // alert(channels)
        localStorage.setItem('PayMentChannels', JSON.stringify(this.state.channels));
        // localStorage.removeItem('Tempchannels')
        console.log("onDeleteChange Called ", this.state.value)
    }

    onValueChange = event => setValue(event.target.value);

    handleSubmit = async (event) => {
        event.preventDefault();
        setErrors("");
        if (Number.isNaN(parseInt(this.state.channelId, 10))) {
            setErrors("Enter Interger channel Id")
        }
        else {
            let value = await GetChannelDetails(this.state.channelId)
            setDetails(value);
        }
    }

    onOptionChange = event => setChannelId(event.target.value)

    return() {
        return (
            <>
                <h2>Channel Details</h2>
                <div>
                    <div>
                        <h3>Add or remove channel (using channel id) from localStorage</h3>
                        <form id="form">
                            <input value={this.state.value} type="text" onChange={this.onValueChange} />
                            <button onClick={this.onAddChange}>Add Channel Id </button>
                            <button onClick={this.onDeleteChange}>Delete Channel Id </button>
                        </form>
                        <p>channel -> '{JSON.stringify(this.state.channels)}'</p>
                    </div>
                    <div>
                        <form id="form" onSubmit={this.handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="channelId">Channel Id</label>
                                <div className="field">
                                    <ChannelList onChange={this.onOptionChange} />
                                </div>
                                {this.state.errors && <div className="alert alert-danger">{this.state.errors}</div>}
                            </div>
                            <button id="button">Get Channel Details</button>
                        </form>
                        {(this.state.errors === '') && (
                            <div >
                                <h2>Channel Details of channel id -> {this.state.channelId}</h2>
                                <ManageDetailsView details={this.state.details} />
                            </div>
                        )}
                    </div>
                    {/* <EditFiles /> */}
                </div>
            </>
        );
    }
};

export default ChannelDetailsPage;
