import React from "react";
// import { toast } from "react-toastify";
import { GetChannelDetails, IsMyChannel } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
import ChannelList from "./ChannelList";
import { FetchAccount } from "./MetaMaskFetch";
import IsNumber from "../Api/Utils";

class ChannelDetailsPage extends React.Component {
    state = {
        errors: false,
        channelId: "",
        details: "",
        value: "",
        operation: "",
        sender: "",
        channels: [],

    }

    componentDidMount() {
        // const slug = this.props.match.params.channel_id;
        // console.log(this.props)
        // console.log(slug);
        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
                console.log(this.state.sender)
                let key = this.state.sender + 'active_channel_ids'
                this.setState({ 'channels': JSON.parse(localStorage.getItem(key) || "[]") })

            }

        })
            .catch(err => {
                alert("check metamask connectivity")
                console.log(err)
            })
    }

    onAddChange = async (event) => {
        event.preventDefault();
        if (this.state.sender === undefined || this.state.sender === null) {
            alert("Check MetaMask Connectivity")
        }
        else {
            if (Number.isNaN(parseInt(this.state.value, 10))) {
                this.setState({ 'errors': "Enter Interger channel Id" })
            }
            else {
                let key = this.state.sender + 'active_channel_ids'
                let flag = await IsMyChannel(this.state.value, this.state.sender)
                var index = this.state.channels.indexOf(this.statevalue);
                // if (flag && index < 0) {
                //     this.state.channels.push(this.state.value)
                //     localStorage.setItem(key, JSON.stringify(this.state.channels));
                // }
                console.log("onAddChange Called ", this.state.value, flag, index)
            }
        }
    }

    onDeleteChange = async (event) => {
        event.preventDefault();
        if (this.state.sender === undefined || this.state.sender === null) {
            alert("Check MetaMask Connectivity")
        }
        else {
            let key = this.state.sender + 'active_channel_ids'
            let flag = await IsMyChannel(this.state.value, this.state.sender)
            var index = this.state.channels.indexOf(this.state.value);
            if (!flag && index > -1) {
                this.state.channels.splice(index, 1);
            }
            localStorage.setItem(key, JSON.stringify(this.state.channels));
            console.log("onDeleteChange Called ", this.state.value)
        }
    }

    handleChannelIdUpdate = async (event) => {
        event.preventDefault();
        this.setState({ 'errors': "" });
        console.log("hello", this.state.channelId)
        if (this.state.operation === "") {
            this.setState({ 'errors': "select radio button" })
        }
        else if (Number.isNaN(parseInt(this.state.value, 10))) {
            this.setState({ 'errors': "Enter Interger channel Id" })
        }
        else {
            alert("bingo")
        }
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        this.setState({ 'errors': "" });
        console.log("hello", this.state.channelId)
        if (Number.isNaN(parseInt(this.state.channelId, 10))) {
            this.setState({ 'errors': "Enter Interger channel Id" })
        }
        else {
            let value = await GetChannelDetails(this.state.channelId)
            this.setState({ 'details': value })
        }
    }

    onValueChange = async (event) => this.setState({ 'value': event.target.value });

    onOptionChange = async (event) => this.setState({ 'channelId': event.target.value });

    onRadioChange = async (event) => this.setState({ 'operation': event.target.value });

    render() {
        return (
            <>
                <h2>Channel Details</h2>
                <div>
                    <div>
                        <h3>Add or remove channel (using channel id) from localStorage</h3>
                        <form id="form" onSubmit={this.handleChannelIdUpdate}>
                            <input value={this.state.value} type="text" onChange={this.onValueChange} />
                            <p>{this.state.value}</p>
                            <div onChange={this.onRadioChange}>
                                <input type="radio" value="add" name="gender" /> Add
                                <input type="radio" value="delete" name="gender" /> Delete
                            </div>
                            <button id="button">Update Channel Id List</button>
                        </form>
                    </div>
                    <div>
                        <form id="form" onSubmit={this.handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="channelId">Channel Id</label>
                                <div className="field">
                                    <ChannelList sender={this.state.sender} onChange={this.onOptionChange} />
                                </div>
                                {this.state.errors && <div className="alert alert-danger">{this.state.errors}</div>}
                            </div>
                            <button id="button">Get Channel Details</button>
                        </form>
                        {(this.state.errors === '') && (
                            <div >
                                <h2>Channel Details of channel id -> {this.state.channelId}</h2>
                                <ManageDetailsView details={this.state.details} />
                            </div>
                        )}
                    </div>
                    {/* <EditFiles /> */}
                </div>
            </>
        );
    }
};

export default ChannelDetailsPage;
