class RedisFunctionalTesting extends React.Component {
    state = {
        errors: '',
        channelId: '',
        reqType: '',
        requestData: '',

    }

    onChange = async (event) => this.setState({ 'lastState': event.target.value });

    handleConfirmChannel = async (event) => {
        event.preventDefault();
        this.setState({ 'errors': "" })
        if (Number.isNaN(parseInt(this.state.reqType, 10))) {
            this.setState({ 'errors': "Enter Integer amount(in wei unit)" })
        }
        else {
            let sender = await FetchAccount()
            var bodyFormData = new FormData();
            bodyFormData.set('channel_id', this.state.channelId);
            bodyFormData.set('address', sender);
            bodyFormData.set('request_type', this.state.reqType);
            bodyFormData.set('request_data', this.state.requestData);
            const resp = await axios({
                method: 'post',
                url: RedisHandlerURL + '/publish',
                // url: RedisHandlerURL + '/subscribe',
                // url: RedisHandlerURL + '/unsubscribe',
                data: bodyFormData
            })
            if (resp.status === 200) {
                console.log(resp.data)
            }
            // console.log(await GenerateSignatures(this.state.lastState[1], this.state.reqType, this.state.requestData))
            // ChannelConfirm(this.state.channelId, this.state.amount)
        }
        // this.setState({ 'reqType': "" })
        // this.setState({ 'requestData': "" })


    }
    handlePendingState = async (event) => {
        event.preventDefault();
        let sender = await FetchAccount()
        let response = await FetchRequests(sender, STATE_TYPE.Response)
        console.log(response)
        localStorage.setItem('PendingResponse', JSON.stringify(response));
    }
    render() {
        return (
            <div>
                <h3>
                    <nav>
                        <NavLink activeStyle={activeStyle} exact to="/pendingStates">
                            Pending States
                        </NavLink>
                        {" | "}
                        <NavLink activeStyle={activeStyle} to="/channeldetails">
                            Last State
                        </NavLink>
                    </nav>
                </h3>
                <div>
                    <h2>Pending States</h2>
                    <div>
                        <h1>Hello React with Local Storage!</h1>
                        <form id="form" onSubmit={this.handlePendingState}>
                            <input type="submit" value="Refresh Pending States" className="btn btn-primary" />
                        </form>
                        {

                            console.log(this.state.pendingStates)
                        }
                        <table id="table" className="table">
                            <thead>
                                <tr>
                                    <th>Decline State</th>
                                    <th>Accept State</th>
                                    <th>State Details</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    Object.keys(this.state.pendingStates).map(request => {
                                        return (
                                            <tr key={request}>
                                                <td>
                                                    <button
                                                        className="btn btn-outline-danger"
                                                        onClick={() => onDeleteClick(request)}
                                                    >
                                                        Delete Request
                                            </button>
                                                </td>
                                                <td>
                                                    <button
                                                        className="btn btn-outline-danger"
                                                        onClick={() => onAcceptClick(this.state.pendingStates[request], request)}
                                                    >
                                                        Accept Request
                                            </button>
                                                </td>
                                                <td><span>{this.state.pendingStates[request]}</span></td>
                                                {/* <td>{JSON.parse(this.state.response[request])}</td> */}
                                                {/* <td>{this.state.response[request].count}</td> */}
                                                {/* <td>{this.state.response[request]['count']}</td> */}
                                                {/* <td>{this.state.response[request].count}</td> */}
                                            </tr>
                                        )

                                    }
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                    <div>
                        <h1>RedisFunctionalTesting</h1>
                        {/* <ShowState state={this.state.lastState} /> */}
                    </div>
                    <div className="mt-2" ></div>
                    <form id="form" className="mt-2" onSubmit={this.handleConfirmChannel}>
                        <div className="form-group">
                            <label htmlFor="channelId">Channel Id</label>
                            <div className="field">
                                <input
                                    id="channelId"
                                    onChange={event => this.setState({ channelId: event.target.value })}
                                    type="text"
                                    name="channelId"
                                    className="form-control"
                                    value={this.state.channelId}
                                />
                            </div>
                        </div>
                        <div className="form-group">
                            <label htmlFor="reqType">Request Type</label>
                            <div className="field">
                                <input
                                    id="reqType"
                                    type="text"
                                    onChange={event => this.setState({ reqType: event.target.value })}
                                    name="reqType"
                                    className="form-control"
                                    value={this.state.reqType}
                                />
                            </div>
                            {/* {this.state.amountError && <div className="alert alert-danger">{this.state.amountError}</div>} */}
                        </div>
                        <div className="form-group">
                            <label htmlFor="requestData">Request Data</label>
                            <div className="field">
                                <input
                                    id="requestData"
                                    type="text"
                                    onChange={event => this.setState({ requestData: event.target.value })}
                                    name="requestData"
                                    className="form-control"
                                    value={this.state.requestData}
                                />
                            </div>
                            {this.state.errors && <div className="alert alert-danger">{this.state.errors}</div>}
                        </div>
                        <input type="submit" value="Submit Transaction" className="btn btn-primary" />
                    </form>
                </div>
            </div >
        );
    }
};

