import React from "react";
// import { toast } from "react-toastify";
import { GetChannelDetails } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
import ChannelList from "./ChannelList";
import { FetchAccount } from "./MetaMaskFetch";
import FilterEvents from "../contract/CreateInstance";


async function onDeleteClick(sender) {
    let events = await FilterEvents(sender)
    console.log(events)
}

class FetchContractTxn extends React.Component {
    state = {
        sender: "",
    }

    componentDidMount() {

        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
            }

        })
            .catch(err => {
                alert("check metamask connectivity")
                console.log(err)
            })
    }

    handleEventFilter = async (event) => {
        event.preventDefault();
        let events = await FilterEvents(this.state.sender)
        console.log(events)
    }

    render() {
        return (
            <>
                <h2>Event Filter Outcome</h2>
                <div>
                    <div>
                        <p><button onClick={() => onDeleteClick(this.state.sender)}>Apply Event Filter </button></p>
                    </div>
                </div>
            </>
        );
    }
};

export default FetchContractTxn;
