import React from "react";
// import { toast } from "react-toastify";
import { GetChannelDetails, IsMyChannel } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
import ChannelList from "./ChannelList";
import { FetchAccount } from "./MetaMaskFetch";

class ActiveChannelPage extends React.Component {
    state = {
        errors: false,
        channelId: "",
        onChainState: "",
        offChainState: "",
        value: "",
        operation: "",
        sender: "",
        channels: [],

    }

    componentDidMount() {
        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
                console.log(this.state.sender)
                let key = this.state.sender + 'active_channel_ids'
                this.setState({ 'channels': JSON.parse(localStorage.getItem(key) || "[]") })
                this.setState({ 'channelId': this.props.match.params.channel_id })
                console.log("channel Id", this.state.channelId);
                this.fetchOnChainDetails().then(onChainState => {
                    this.setState({ 'onChainState': onChainState })
                }).catch(err => {
                    alert("on chain state issue")
                    console.log(err)
                })
                this.fetchOffChainDetails().then(offChainState => {

                    this.setState({ 'offChainState': offChainState })
                }).catch(err => {
                    alert("off chain state issue")
                    console.log(err)
                })
            }

        }).catch(err => {
            alert("check metamask connectivity")
            console.log(err)
        })
    }

    fetchOnChainDetails = async (event) => {
        let value = await GetChannelDetails(this.state.channelId)
        return value;
    }

    fetchOffChainDetails = async (event) => {
        let key = this.state.sender + this.state.channelId + '_last_signed_state'
        let value = localStorage.getItem(key)
        if (value !== null || value !== undefined) {
            let splitstate = value.split("_");
            let details_div = {
                'Alice Id': details['alice']['id'],
                'Alice Cash': details['alice']['cash'],
                'Alice Input Flag': String(details['alice']['waitForInput']),
                'Bob Id': details['bob']['id'],
                'Bob Cash': details['bob']['cash'],
                'Bob Input Flag': String(details['bob']['waitForInput']),
                'Timeout': details['timeout'],
                'Status': details['status'],
                'Published Tx Count': details['publish_tx_count'],
                'Money': details['money']
            }
            console.log(splitstate)
            return value
        }
        return value;
    }

    render() {
        return (
            <>
                <h2>Channel Id: {this.state.channelId}</h2>
                <div>
                    <div>
                        <p>On Chain State</p>
                        <ManageDetailsView details={this.state.onChainState} />
                    </div>
                    <div>
                        <p>Off Chain State</p>
                        <p> {this.state.offChainState}</p>
                    </div>
                </div>
            </>
        );
    }
};

export default ActiveChannelPage;
