import React from "react";
// import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import './UserList.css'


class UserList extends React.Component {
    state = {
        errors: false,
        channelId: "",
        onChainState: false,
        offChainState: false,
        formFlag: false,
        sender: "",
        action: '',
        aliceCash: '',
        bobCash: '',
    }

    render() {
        return (
            <div>
                <h2>User DataBase</h2>
                <Link to="/adduser">
                    <button id="AddUser">
                        <span>Add New User</span>
                    </button>
                </Link>
                <table id='table' className="table">
                    <thead>
                        <tr>
                            <th>&nbsp;</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Category</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Object.keys(this.props.users).map(user => {
                            return (
                                <tr key={user.id}>
                                    <td>
                                        {/* <Link to="/openchannel">
                                        <button id="AddUser" address={props.users[user][0]}>
                                            <span>CreateChannel</span>
                                        </button>
                                    </Link> */}
                                        <button
                                            className="btn btn-outline-danger"
                                            onClick={() => {
                                                const min = 10000000;
                                                const max = 100000000;
                                                const rand = Math.floor(min + Math.random() * (max - min));
                                                console.log(rand)
                                                let cash = prompt("Please channel money:", '111');
                                                if (Number.isNaN(parseInt(cash, 10))) {
                                                    alert("Enter Interger Amount")
                                                }
                                                else {
                                                    this.props.createNewChannel(rand, user.address, cash).then(() => {
                                                        // Add Channel Id in List
                                                        // set default state as an last state
                                                        // set listening socket from UserList 
                                                        toast.success("Channel Created with id " + rand);
                                                    });
                                                }

                                            }}
                                        >
                                            CreateChannel
                                </button>
                                    </td>
                                    <td>{user}</td>
                                    <td>{this.props.users[user][0]}</td>
                                    <td>{this.props.users[user][1]}</td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
        );
    }
}


export default UserList;
