import React from "react";
import { FetchAccount } from "./MetaMaskFetch";
import FilterEvents from "../contract/CreateInstance";


class FetchContractTxn extends React.Component {
    state = {
        sender: "",
    }

    componentDidMount() {

        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
            }

        })
            .catch(err => {
                alert("check metamask connectivity")
                console.log(err)
            })
    }

    handleEventFilter = async (event) => {
        event.preventDefault();
        let events = await FilterEvents(this.state.sender)
        events.forEach(element => {
            console.log(element.event)
            console.log(element.returnValues)
            // console.log(element)
            // console.log(element)
            /*
            add handler for each event and add respected click handler with possible cases
                event EventChannelInitializing(address libSig);
                event EventInitializing(address addressAlice, address addressBob, uint channel_id);
                event EventInitialized(uint cashAlice, uint cashBob, uint channel_id);
                event EventRefunded(uint channel_id);
                event EventClosing(uint channel_id);
                event EventClosed(uint channel_id);
                event EventNotClosed(uint channel_id);
            */
        });
    }

    render() {
        return (
            <>
                <h2>Event Filter Outcome</h2>
                <div>
                    <div>
                        <p><button onClick={this.handleEventFilter}>Apply Event Filter </button></p>
                    </div>
                </div>
            </>
        );
    }
};

export default FetchContractTxn;
