import React from "react";
import { FetchAccount } from "./MetaMaskFetch";
import FilterEvents from "../contract/CreateInstance";


class FetchContractTxn extends React.Component {
    state = {
        sender: "",
        key: '',
        fetchedEvent: null,
    }

    componentDidMount() {

        FetchAccount().then(sender => {
            console.log(sender)
            if (sender) {
                this.setState({ 'sender': sender })
                this.setState({ 'key': this.state.sender + 'contract_events' })
            }
            console.log(this.state.key)
        })
            .catch(err => {
                alert("check metamask connectivity")
                console.log(err)
            })
    }

    fetchEventFilter = async (event) => {
        event.preventDefault();
        let events = await FilterEvents(this.state.sender)

        // localStorage.setItem(this.state.key, JSON.stringify(events));
        this.setState({ 'fetchedEvent': [] })
        events.forEach(element => {
            console.log(element.event)
            if (element.event === 'EventChannelInitializing') {
                // event EventChannelInitializing(address libSig);
                console.log("Mutli channel contract initiaized using Signature Lib at ", element.returnValues[0])
            }
            else if (element.event === 'EventInitializing') {
                // event EventInitializing(address addressAlice, address addressBob, uint channel_id);
                console.log("channel open initated by", element.returnValues[0], "with ", element.returnValues[1], "and channel id is ", element.returnValues[2])
            }
            else if (element.event === 'EventInitialized') {
                // event EventInitialized(uint cashAlice, uint cashBob, uint channel_id);
            }
            else if (element.event === 'EventRefunded') {
                // event EventRefunded(uint channel_id);
            }
            else if (element.event === 'EventClosing') {
                // event EventClosing(uint channel_id);
            }
            else if (element.event === 'EventClosed') {
                // event EventClosed(uint channel_id);
            }
            else if (element.event === 'EventNotClosed') {
                // event EventNotClosed(uint channel_id);
            }
        });
        localStorage.setItem(this.state.key, JSON.stringify(this.state.fetchedEvent));
    }

    handlerEvents = async (event) => {
        event.preventDefault();

    }

    render() {
        return (
            <>
                <h2>Event Filter Outcome</h2>
                <div>
                    <div>
                        <p><button onClick={this.fetchEventFilter}>Apply Event Filter </button></p>
                        <table id="table" className="table">
                            <thead>
                                <tr>
                                    <th>Accept State</th>
                                    <th>State Details</th>
                                    <th>Last Signed State</th>
                                </tr>
                            </thead>
                            <tbody>
                                {

                                    JSON.parse(localStorage.getItem(this.state.key)) && Object.keys(JSON.parse(localStorage.getItem(this.state.key))).map(request => {
                                        console.log("Hello11", request, this.state.fetchedEvent)
                                        return (

                                            <tr key={request}>
                                                <td>
                                                    <button
                                                        className="btn btn-outline-danger"
                                                        onClick={() => this.handlerEvents(this.state.fetchedEvent[request], request)}
                                                    >
                                                        Accept Request
                                            </button>
                                                </td>
                                                <td><span>{JSON.parse(localStorage.getItem(this.state.key))[request]}</span></td>
                                                <td>
                                                    <button className="btn btn-outline-danger"  >
                                                        Show Last Signed State
                                                </button>
                                                </td>
                                            </tr>
                                        )

                                    }
                                    )
                                }
                            </tbody>
                        </table>
                        {console.log("hello", this.state.fetchedEvent)}
                    </div>
                </div>
            </>
        );
    }
};

export default FetchContractTxn;
