import React, { useState } from "react";
// import courseStore from "../stores/courseStore";
// import { toast } from "react-toastify";
// import * as courseActions from "../actions/courseActions";
import { GetChannelDetails } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
import { fetchLastState } from "../Api/FetchBrowserData";

const ChannelDetailsPage = props => {
    const [errors, setErrors] = useState(false);
    const [channelId, setChannelId] = useState("");
    const [details, setDetails] = useState("");
    const [value, setValue] = useState("");
    const [channels, setChannels] = React.useState(localStorage.getItem('Tempchannels') || new Array());
    // const [course, setCourse] = useState({
    //     id: null,
    //     slug: "",
    //     title: "",
    //     authorId: null,
    //     category: ""
    // });
    React.useEffect(() => {
        // localStorage.removeItem('Tempchannels')
        localStorage.setItem('Tempchannels', channels);
    }, [channels]);
    const onChange = event => {
        setChannels(channels.push("hello"))
        alert(channels)
        // alert(newChannels)
        // setChannels(newChannels);
    }
    const onValueChange = event => setValue(event.target.value);

    async function handleSubmit(event) {
        event.preventDefault();
        setErrors("");
        if (Number.isNaN(parseInt(channelId, 10))) {
            setErrors("Enter Interger channel Id")
        }
        else {
            let value = await GetChannelDetails(channelId)
            setDetails(value);
        }
        setChannelId("")
    }

    return (
        <>
            <h2>Channel Details</h2>
            <div>
                <div>
                    <h1>Hello React with Local Storage!</h1>
                    <form onSubmit={onChange}>
                        <input value={value} type="text" onChange={onValueChange} />
                        <p>{value}</p>
                        <button >Add Channel Id</button>
                    </form>
                    <p>channel -> '{channels}'</p>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="channelId">Channel Id</label>
                        <div className="field">
                            <input id="channelId" name="channelId"
                                type="text"
                                placeholder="channel id(integer value)"
                                className="form-control"
                                value={channelId}
                                onChange={event => setChannelId(event.target.value)} />
                        </div>
                        {errors && <div className="alert alert-danger">{errors}</div>}
                    </div>
                    <button>Get Channel Details</button>
                </form>
                {(errors === '') && (
                    <div >
                        <h2>Channel Details of channel id -> {channelId}</h2>
                        <ManageDetailsView details={details} />
                    </div>
                )}
                <button onClick={fetchLastState}>Fetch Last State</button>
            </div>
        </>
    );
};

export default ChannelDetailsPage;
