import React from "react";
// import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import './UserList.css'


class UserList extends React.Component {
    state = {
        errors: false,
        channelId: "",
        onChainState: false,
        offChainState: false,
        formFlag: false,
        sender: "",
        action: '',
        aliceCash: '',
        bobCash: '',
    }

    handleCreateChannel = async (event) => {
        console.log("this.state.action", address)
        let path = "/openchannel"
        this.props.history.push({
            pathname: path,
            state: {
                address: this.state.address,
            }
        })
    }

    render() {
        return (
            <div>
                <h2>User DataBase</h2>
                <Link to="/adduser">
                    <button id="AddUser">
                        <span>Add New User</span>
                    </button>
                </Link>
                <table id='table' className="table">
                    <thead>
                        <tr>
                            <th>&nbsp;</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th>Category</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Object.keys(this.props.users).map(user => {
                            return (
                                <tr key={user.id}>
                                    <td>
                                        {/* <Link to="/openchannel">
                                        <button id="AddUser" address={props.users[user][0]}>
                                            <span>CreateChannel</span>
                                        </button>
                                    </Link> */}
                                        <button
                                            className="btn btn-outline-danger"
                                            onClick={() => {
                                                const min = 10000000;
                                                const max = 100000000;
                                                const rand = Math.floor(min + Math.random() * (max - min));
                                                console.log(rand)
                                                var person = prompt("Please enter your name:", "Harry Potter");
                                                if (person == null || person == "") {
                                                    txt = "User cancelled the prompt.";
                                                } else {
                                                    txt = "Hello " + person + "! How are you today?";
                                                }
                                                this.props.createNewChannel(rand, user.address).then(() => {
                                                    // Add Channel Id in List
                                                    // set default state as an last state
                                                    // set listening socket from UserList 
                                                    toast.success("Channel Created with id " + rand);
                                                });
                                            }}
                                        >
                                            CreateChannel
                                </button>
                                    </td>
                                    <td>{user}</td>
                                    <td>{this.props.users[user][0]}</td>
                                    <td>{this.props.users[user][1]}</td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
        );
    }
}


export default UserList;
