import React from "react";
import { FetchAccount } from "../components/MetaMaskFetch";
import '../components/Form.css'
import STATE_TYPE from "../RequestType";
import { FetchRequests, DeleteRequest } from "./GetSetData";
import { FetchOtherParty, IsValidSignature } from "../contract/contract_transaction";

async function onDeleteClick(sender, index) {
    let response = await DeleteRequest(sender, STATE_TYPE.Response, index)
    console.log(response)
}

async function onShowLastState(sender, request) {
    let channel_id = request.split('_')[0]
    let key = sender + channel_id + "_last_signed_state"
    alert(localStorage.getItem(key) || "No Signed State Defined")
}

async function onAcceptClick(sender, request, index) {
    let splitstate = request.split("_");
    let state_obj = {
        'channel_id': Number.parseInt(splitstate[0]),
        'count': Number.parseInt(splitstate[1]),
        'alice_cash': Number.parseInt(splitstate[2]),
        'bob_cash': Number.parseInt(splitstate[3]),
        'sig': splitstate[4],
    }
    console.log(state_obj.channel_id, state_obj.count, state_obj.alice_cash, state_obj.bob_cash, state_obj.sig)
    const TheOtherParty = await FetchOtherParty(state_obj.channel_id, sender)
    let checkSig = await IsValidSignature(TheOtherParty, state_obj.channel_id, state_obj.count, state_obj.alice_cash, state_obj.bob_cash, state_obj.sig)

    if (checkSig === true) {
        let key = sender + state_obj.channel_id + "_last_signed_state"
        request = request.replace('"', '')
        localStorage.setItem(key, request);
        alert("valid signature")
    }
    else {
        alert("invalid signature")
    }
}

class PendingResponses extends React.Component {
    state = {
        pendingResponse: null,
        sender: null,
    }

    componentDidMount() {
        FetchAccount()
            .then(sender => {
                if (sender) {
                    this.setState({ 'sender': sender })
                    console.log(this.state.sender)
                }
                FetchRequests(sender, STATE_TYPE.Response)
                    .then(response => {
                        this.setState({ pendingResponse: response })
                    })
                    .catch(err => {
                        alert("error occured while fetching pending response")
                        console.log(err)
                    })
            })
            .catch(err => {
                alert("check metamask connectivity")
                console.log(err)
            })
    }

    render() {
        return (
            <div>
                <h2>New State Response</h2>
                <div>
                    <table id="table" className="table">
                        <thead>
                            <tr>
                                <th>Decline State</th>
                                <th>Accept State</th>
                                <th>Channel Id</th>
                                <th>Count</th>
                                <th>Alice Cash</th>
                                <th>Bob Cash</th>
                                <th>Last Signed State</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.pendingResponse && Object.keys(this.state.pendingResponse).map(request => {
                                    return (
                                        <tr key={request}>
                                            <td>
                                                <button
                                                    className="btn btn-outline-danger"
                                                    onClick={() => onDeleteClick(this.state.sender, request)}
                                                >
                                                    Delete Request
                                            </button>
                                            </td>
                                            <td>
                                                <button
                                                    className="btn btn-outline-danger"
                                                    onClick={() => onAcceptClick(this.state.sender, this.state.pendingResponse[request], request)}
                                                >
                                                    Accept Request
                                            </button>
                                            </td>
                                            <td><span>{this.state.pendingResponse[request].split('_')[0]}</span></td>
                                            <td><span>{this.state.pendingResponse[request].split('_')[1]}</span></td>
                                            <td><span>{this.state.pendingResponse[request].split('_')[2]}</span></td>
                                            <td><span>{this.state.pendingResponse[request].split('_')[3]}</span></td>
                                            <td>
                                                <button className="btn btn-outline-danger" onClick={() => onShowLastState(this.state.sender, this.state.pendingResponse[request])} >
                                                    Show Last Signed State
                                                </button>
                                            </td>
                                        </tr>
                                    )

                                }
                                )
                            }
                        </tbody>
                    </table>
                </div>

            </div >
        );
    }
};

export default PendingResponses;
