import React from "react";
// import PropTypes from "prop-types";
// import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import './UserList.css'
import { FetchRequests } from "../redisApi/GetSetData";

async function FetchPendingState(props) {
    return (
        <div>

        </div>
    );
}

function onDeleteClick(response_array, value) {
    console.log(response_array, value)
    console.log("I'm Happy One")
    console.log("value")
    response_array.splice(value, 1);
    localStorage.setItem('response', JSON.stringify(response_array));
    console.log(response_array.length)
}

async function onAcceptClick(request) {
    console.log(request)
    let ReqObject = JSON.parse(request)
    console.log(ReqObject.channel_id, ReqObject.count, ReqObject.Alice_Cash, ReqObject.Bob_Cash, ReqObject.signature)
    const sender = await FetchAccount()
    const flag = await IsValidSignature(sender, ReqObject.count, ReqObject.Alice_Cash, ReqObject.Bob_Cash, ReqObject.signature)
    console.log(flag)
    let result = await GenerateSignatures(ReqObject.count, ReqObject.Alice_Cash, ReqObject.Bob_Cash)
    console.log(result)
    ReqObject.signature = result.alice
    Socket.emit('publishToChannel', 'newChannel', JSON.stringify(ReqObject));
    console.log("all Done")
}

class PendingState extends React.Component {
    state = {
        channelId: "",
        channelIdError: "",
        amount: '',
        amountError: "",
        pendingStates: JSON.parse(localStorage.getItem("pendingState") || "[]"),
    };
    handleConfirmChannel = async (event) => {
        event.preventDefault();
        // this.setState({ 'channelIdError': "" })
        // this.setState({ 'amountError': "" })
        // if (Number.isNaN(parseInt(this.state.channelId, 10))) {
        //     this.setState({ 'channelIdError': "Enter Interger channel Id" })
        // }
        // else if (Number.isNaN(parseInt(this.state.amount, 10))) {
        //     this.setState({ 'amountError': "Enter Integer amount(in wei unit)" })
        // }
        // else {
        let response = await FetchRequests(1, "0x19DbcfB1E970EB0ac300936824de350EE248e803", 1)
        console.log(response)
        localStorage.setItem('pendingState', JSON.stringify(response));
        // }
        // this.setState({ 'channelId': "" })
        // this.setState({ 'amount': "" })
    }


    render() {
        return (
            <div>
                <h2>Pending States</h2>
                <div>
                    <h1>Hello React with Local Storage!</h1>
                    <form id="form" onSubmit={this.handleConfirmChannel}>
                        <input type="submit" value="Refresh Pending States" className="btn btn-primary" />
                    </form>
                    {console.log(this.state.pendingStates)}
                    <table id="table" className="table">
                        <thead>
                            <tr>
                                <th>Decline State</th>
                                <th>Accept State</th>
                                <th>State Details</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                Object.keys(this.state.pendingStates).map(request => {
                                    return (
                                        <tr key={request}>
                                            <td>
                                                <button
                                                    className="btn btn-outline-danger"
                                                    onClick={() => onDeleteClick(this.state.response, request)}
                                                >
                                                    Delete Request
                                            </button>
                                            </td>
                                            <td>
                                                <button
                                                    className="btn btn-outline-danger"
                                                    onClick={() => onAcceptClick(this.state.response[request])}
                                                >
                                                    Accept Request
                                            </button>
                                            </td>
                                            {/* {this.state.response[request].map(user => {
                                            return (
                                                <td>{user}</td>
                                            );
                                        })} */}
                                            <td><span>{this.state.pendingStates[request]}</span></td>
                                            {/* <td>{JSON.parse(this.state.response[request])}</td> */}
                                            {/* <td>{this.state.response[request].count}</td> */}
                                            {/* <td>{this.state.response[request]['count']}</td> */}
                                            {/* <td>{this.state.response[request].count}</td> */}
                                        </tr>
                                    )

                                }
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
};
export default PendingState;
