        return (
            <div>
                <form onSubmit={this.handleConfirmChannel}>
                    <div className="form-group">
                        <label htmlFor="channelId">Channel Id</label>
                        <div className="field">
                            <input
                                id="channelId"
                                type="text"
                                onChange={event => this.setState({ channelId: event.target.value })}
                                name="channelId"
                                className="form-control"
                                value={this.state.channelId}
                                required
                            />
                        </div>
                        {this.state.channelIdError && <div className="alert alert-danger">{this.state.channelIdError}</div>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Channel Id</label>
                        <div className="field">
                            <input
                                id="amount"
                                type="text"
                                onChange={event => this.setState({ amount: event.target.value })}
                                name="amount"
                                className="form-control"
                                value={this.state.amount}
                                required
                            />
                        </div>
                        {this.state.amountError && <div className="alert alert-danger">{this.state.amountError}</div>}
                    </div>
                    <input type="submit" value="Submit" className="btn btn-primary" />
                </form>


