class ClientClass extends React.Component {
    constructor() {
        super();
        this.state = {
            userId: 'channel',
            response: JSON.parse(localStorage.getItem("response") || "[]"),
            endpoint: "http://127.0.0.1:8001/notifications"
        };
    }

    componentDidMount() {
        const { endpoint } = this.state;
        console.log(this.state.userId)
        if (this.state.userId !== '') {
            //Very simply connect to the socket
            const socket = io.connect(endpoint);
            //Listen for data on the "outgoing data" namespace and supply a callback for what to do when we get one. In this case, we set a state variable
            socket.on("notification", (channel, message) => {
                console.log("Hello ", message)
                this.setState({ response: this.state.response + "\n" + channel + "\n" + message })
            });
            socket.on('connected', () => {
                var msg = "You are now connected for push notifications";
                console.log(msg);
                let newValue = JSON.parse(localStorage.getItem("response") || "[]")
                newValue.push(msg)
                localStorage.setItem('response', JSON.stringify(newValue));
                // this.setState({ response: newValue })
                // Send the user ID
                socket.emit('join', this.state.userId);
            });
        }
    }

    onUserIdChange(event) {
        event.preventdefault();
        { event => this.setState({ userId: event.target.value }) }
    }
    render() {
        const { response } = this.state;
        return (
            <div style={{ textAlign: "center" }}>
                <script src="/socket.io/socket.io.js"></script>
                <script src="http://ajax.googleapis.com/ajax/libs/jquery/1.9.1/jquery.min.js"></script>

                <form id="idForm" onSubmit={this.state.componentDidMount} action="">
                    User ID: <input
                        id="userId"
                        type="text"
                        onChange={this.onUserIdChange}
                        name="userId"
                        className="form-control"
                        value={this.state.userId}
                    /><br />
                    <input id="formButton" type="submit" value="Submit" />
                </form>
                <h2>Messages from server:</h2>
                {/* <ReactSpeedometer
                    maxValue={140}
