import React from 'react';
// import { Tx } from "ethereumjs-tx";
import { contract_address, contract_abi } from "./contract_config";
// import { Web3 } from "web3";
import { aliceAddress, bobAddress } from "./wallet";


// const Tx = require('ethereumjs-tx')
const Web3 = require('web3')
const web3 = new Web3(new Web3(new Web3.providers.HttpProvider("http://localhost:8545")))

const sideChainContract = new web3.eth.Contract(contract_abi, contract_address)
let details_div = ""

const GetChannelDetails = async (channelId) => {
    console.log(channelId)
    // function startExit( bytes prevTx, bytes prevTxProof, uint prevTxBlkNum, bytes tx, bytes txProof, uint txBlkNum)
    try {
        var details = await sideChainContract.methods.getChannel(channelId).call()
        console.log('Alice', details['alice']['id'], details['alice']['cash'], details['alice']['waitForInput']);
        console.log('Bob', details['bob']['id'], details['bob']['cash'], details['bob']['waitForInput']);
        console.log('timeout', details['timeout']);
        console.log('status', details['status']);
        console.log('lastCount', details['publish_tx_count']);
        console.log('Money', details['money']);
        details_div = 'Alice ' + details['alice']['id'] + ' ' + details['alice']['cash'] + ' ' + details['alice']['waitForInput'] + '\n';
        details_div += 'Bob ' + details['bob']['id'] + ' ' + details['bob']['cash'] + ' ' + details['bob']['waitForInput'] + '\n';
        details_div += 'timeout ' + details['timeout'] + '\n';
        details_div += 'status ' + details['status'] + '\n';
        details_div += 'lastCount ' + details['publish_tx_count'] + '\n';
        details_div += 'Money ' + details['money'] + '\n';
        console.log(details_div)
    } catch (error) {
        console.log("contract details are tempered or connectivity issue")
        console.log(error)
    }
}

const CreateNewChannel = async (channelId) => {
    console.log(web3.utils.toChecksumAddress('0xbA886b598e75CD3526F789c8D58B95464e778387'))
    try {
        var txHash = await sideChainContract.methods.openChannel(channelId, web3.utils.toChecksumAddress(bobAddress))
            .send({ from: aliceAddress, gas: 200000 })
        console.log('new channel created, txHash->', txHash.transactionHash);
    } catch (error) {
        console.log("Open Channel request reverted")
        console.log(error)
    }
}

const ChannelConfirm = async (channelId) => {
    try {
        var txHash = await sideChainContract.methods.confirm(channelId)
            .send({ from: aliceAddress, value: web3.utils.toHex(web3.utils.toWei('5', 'gwei')), gas: 200000 })
        console.log('Channel Deposit Confirmed. txHash-> ', txHash.transactionHash);
    } catch (error) {
        console.log("Channel Deposit request reverted")
        console.log(error)
    }
}

const ChannelRefund = async (channelId) => {
    try {
        var txHash = await sideChainContract.methods.refund(channelId)
            .send({ from: bobAddress, gas: 200000 })
        console.log('Channel Refund Confirmed. txHash-> ', txHash.transactionHash);
    } catch (error) {
        console.log("Channel Refund request reverted")
        console.log(error)
    }
}

const IsValidSignature = async () => {
    try {
        var flag = await sideChainContract.methods.isValidSignature("addr", "count", "sender_balance", "recipient_balance", "signature").call()
        console.log('isValidSignature ', flag);
    } catch (error) {
        console.log("contract details are tempered or connectivity issue or invalid arguments passed")
        console.log(error)
    }
}

const ChannelFinalizeClose = async (channelId) => {
    try {
        var txHash = await sideChainContract.methods.finalizeClose(channelId)
            .send({ from: bobAddress, gas: 200000 })
        console.log('Channel Closed. txHash-> ', txHash.transactionHash);
    } catch (error) {
        console.log("Channel finalizeClose request reverted")
        console.log(error)
    }
}

const ChannelClose = async (props) => {
    try {
        var txHash = await sideChainContract.methods.close("channel_id", "count", "sender_balance", "recipient_balance", "signature")
            .send({ from: bobAddress, gas: 200000 })
        console.log('Channel Close request proceed. txHash-> ', txHash.transactionHash);
    } catch (error) {
        console.log("Channel Closed request reverted")
        console.log(error)
    }
}

const Channel = () => {
    return (
        <div>
            <div className="App-content">
                <button onClick={CreateNewChannel}> NewChannel</button>
                {" "}
                <button onClick={ChannelConfirm}> Channel confirm</button>
                {" "}
                <button onClick={GetChannelDetails}> ShowDetails</button>
                {" "}
            </div>
        </div >
    );

};


export { GetChannelDetails, CreateNewChannel, ChannelConfirm, ChannelRefund, IsValidSignature, ChannelFinalizeClose, ChannelClose, Channel }
