            method: 'post',
            url: RedisHandlerURL + '/fetch_states',
            data: bodyFormData
        })
        if (resp.status === 200) {
            console.log(resp.data)
            return resp.data;
        }
        return null;
    } catch (error) {
        console.log("Redis connectivity issue")
        console.log(error)
    }
}

