import React from "react";
// import { toast } from "react-toastify";
import { GetChannelDetails } from "../contract/contract_transaction";
import ManageDetailsView from "./ManageDetailsView";
import ChannelList from "./ChannelList";
import { FetchAccount } from "./MetaMaskFetch";
import FilterEvents from "../contract/CreateInstance";

class FetchContractTxn extends React.Component {
    state = {
        sender: "",
    }

    componentDidMount() {

        FetchAccount().then(sender => {
            if (sender) {
                this.setState({ 'sender': sender })
            }

        })
            .catch(err => {
                alert("check metamask connectivity")
                console.log(err)
            })
    }

    onDeleteChange = async (event) => {
        event.preventDefault();
        let props = {
            sender: this.state.sender
        }
        let events = FilterEvents(props)
        let key = this.state.sender + 'PayMentChannels'
        localStorage.setItem(key, JSON.stringify(this.state.channels));
        console.log("onDeleteChange Called ", this.state.value)
    }

    render() {
        return (
            <>
                <h2>Event Filter Outcome</h2>
                <div>
                    <div>
                        <p><button onClick={this.onDeleteChange}>Apply Event Filter </button></p>
                    </div>
                </div>
            </>
        );
    }
};

export default FetchContractTxn;
